$learning-pass-font-size: 14px;
$pass-font-color: #4b4b4b;


.learn-pass {


  &--container {
    box-sizing: border-box;
    scroll-behavior: smooth;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-family: inherit;

    h2, h3, h4, h5, h6 {
      border: none;
      padding: 0;
    }

    .text-link {
      color: #108aeb;
      cursor: pointer;
      font-weight: 500;
      transition: 0.3s ease all;

      &:hover, &:active, &:focus {
        color: #0f5da5;
        text-decoration: none;
      }
    }

    .btn-rounded {
      border-radius: 16px;
    }

    .btn-bordered {
      border-width: 2px;
      border-style: solid;
    }


    .cell {

      &-sub {
        font-size: 14px;
        font-weight: 500;
        line-height: 1.5;
        font-style: normal;
        letter-spacing: 0;
        text-transform: none;
        margin-bottom: 8px;
        margin-top: 8px;
        color: $pass-font-color;

      }

      &--header {
        font-size: 14px;
        font-weight: 700;
        line-height: 1.3;
        font-style: normal;
        letter-spacing: 0;
        text-transform: none;
        margin-bottom: 8px;
        margin-top: 8px;
        text-align: left;

        @media only screen and (max-width: 767px) {
          text-align: center;
        }

      }

      &--card {
        width: calc(70% / 3);
        padding: 20px;
        display: flex;
        flex-direction: column;
        align-items: center;
        border: 1px solid #eee;

        @media only screen and (max-width: 767px) {
          width: 100%;
          border: none;
        }


      }

      &--one {
        width: 50%;
        display: flex;
        align-items: flex-start;
        flex-direction: column;
        justify-content: center;
        border-right: 1px double #eee;
        border-bottom: 1px double #eee;
        border-top: 1px double #eee;
        padding: 15px 20px;

        @media only screen and (max-width: 992px) {
          width: 30%;
          border: none;
        }

        @media only screen and (max-width: 767px) {
          background: #efefef;
          width: 100%;
        }

      }

      &--odd {
        background: #f4fbff;

      }

      &--even {
        background: #e7f4fc;
      }

    }


  }


  &-btn {
    display: inline-block;
    font-size: $learning-pass-font-size;
    padding: 5px 15px 3px;
    min-width: 72px;
    font-weight: 700;
    text-align: center;
    user-select: none;
    cursor: pointer;
    transition: 0.3s ease all;
    margin-top: 10px;

    &:hover, &:active, &:focus {
      text-decoration: none;
    }
  }


  //  Start of Learn Pass Poster Section

  &__cover {
    background-size: cover !important;
    background-position: center !important;
    height: 350px;
    display: flex;
    align-items: center;
    color: white;

    @media only screen and (max-width: 992px) {
      min-height: 700px;

    }


    &--section {

    }

    &--container {
      height: 260px;
      display: flex;
      justify-content: space-around;
      align-items: center;

      @media only screen and (max-width: 992px) {
        flex-direction: column;
        justify-content: center;

      }

    }

    &--poster {
      width: 30%;

      @media only screen and (max-width: 992px) {
        width: 100%;
      }


      img {
        height: 100%;
        width: 100%;
        object-fit: cover;

        @media only screen and (max-width: 992px) {
          object-fit: contain;
        }

      }

    }

    &--contents {
      width: 50%;

      @media only screen and (max-width: 992px) {
        width: 70%;
      }

      @media only screen and (max-width: 767px) {
        display: flex;
        flex-direction: column;
        align-items: center;
        width: 82%;
      }


    }


    &--header {
      font-weight: 900;
      text-transform: none;
      margin-bottom: 8px;
      margin-top: 8px;
      user-select: none;
      font-size: 30px;
      border: none;
      text-align: left;

      @media only screen and (max-width: 992px) {
        text-align: center;
      }
      @media only screen and (max-width: 576px) {
        font-size: 25px;

      }

    }

    &--sub {
      font-weight: 400;
      font-size: $learning-pass-font-size;

      @media only screen and (max-width: 767px) {
        text-align: center;
      }
    }


    &--btn {
      color: white;
      border-color: white;
      background-color: rgba(0, 0, 0, 0);

      &:hover, &:active, &:focus {
        color: $pass-font-color;
        background-color: white;
      }

    }

  }

  //  End of Learn Pass Poster Section


  //  Start of Learn Pass Stats

  &__stats {

    &--section {
      margin-top: 15px;
    }

    &--container {

      @media only screen and (max-width: 767px) {
        width: 80%;
        margin: 0 auto;
      }

    }


  }

  &__item {
    display: flex;

    @media only screen and (max-width: 767px) {
      flex-direction: column;
    }

    &--cell {
      margin: 1px;
      @media only screen and (max-width: 767px) {
        margin: 0;
      }
    }
  }


  &__card {

    &--header {
      color: #2c2c2c;
      font-size: 20px;
      font-weight: 700;
      line-height: 1.3;
      font-style: normal;
      letter-spacing: 0;
      text-transform: none;
      margin: 8px 0;
    }

    &--text {
      font-size: 14px;
      font-weight: 500;
      text-align: center;
      line-height: 1.5;
      font-style: normal;
      letter-spacing: 0;
      text-transform: none;
      margin: 8px 0;
      color: $pass-font-color;

    }

    &--price {


    }

    &--btn {
      background-color: #1473e6;
      border-color: #1473e6;
      color: white;
      padding: 4px 15px;
      border-radius: 30px;
      transition: 0.3s ease all;

      &:hover, &:focus, &:active {
        background-color: #1455A9;
        border-color: #1455A9;
        color: white;
      }
    }

    &--tick {
      color: darkgreen;
      font-size: 40px;
    }


  }

  /*End of Learning pass stats*/


  /*Start of Learning pass contact*/

  &__contact {
    margin-top: 15px;
    background-size: auto !important;
    background-repeat: no-repeat !important;
    min-height: initial !important;


    &--container {

    }

    &--label {
      width: 50%;
      color: $pass-font-color;
      line-height: 1.4;
      margin-left: 10px;
      text-align: left !important;

      @media only screen and (max-width: 767px) {
        font-size: 25px;
        width: 65%;
      }

    }

    &--btn {
      background-color: rgba(0, 0, 0, 0);
      border-color: $pass-font-color;
      color: $pass-font-color;
      margin-left: 10px;

      &:hover, &:active, &:focus {
        color: white;
        background-color: $pass-font-color;
      }
    }

  }


}
